import React from 'react';
import { useFormik } from 'formik';
import * as Yup from 'yup';

// const validationSchema = Yup.object({
//   login: Yup.string().required('Please enter the login').min(10, 'Your login should be 10 characters or more'),
//   password: Yup.string().required('Please enter the password').min(8, 'Your password should be at least 8 characters')
// });
const validate = values => {
  const errors = {};

  if (!values.firstName) {
    errors.firstName = 'Required';
  } else if (values.firstName.length > 15) {
    errors.firstName = 'Must be 15 characters or less';
  }

  if (!values.lastName) {
    errors.lastName = 'Required';
  } else if (values.lastName.length > 20) {
    errors.lastName = 'Must be 20 characters or less';
  }

  if (!values.email) {
    errors.email = 'Required';
  } else if (!/^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,4}$/i.test(values.email)) {
    errors.email = 'Invalid email address';
  }

  return errors;
};

const Form = () => {

  const formik = useFormik({
    initialValues: {
      login: '',
      password: '',
    },
    validationSchema: {
      login: Yup.string()
      .required()
      .min(10),
      password: Yup.string()
      .required()
      .min(8),
  },
    onSubmit: values => {
      alert(JSON.stringify(values))
    }
  });

  return (
  <div>
      <h1>Check components/Form.js file</h1>
      <p>Below you fill find register and login forms.</p>
      <p>Validate values of each field on your preferences. The more secure the better.</p>
      <p>After user submitted register form inform him about it and store user data somewhere.</p>
      <p>When submiting login form check if such a user has been registered.</p>
      <p>If user exists hide all the forms and show a test 'Logged in successfully'</p>
      <p>If user does not exist then show an error message below login button</p>
      <div onSubmit={formik.handleSubmit}>
        <h1>Register</h1>
        <input 
          placeholder="Login"
          type="text" 
          name="login" 
          onChange={formik.handleChange}
          onBlur={formik.handleBlur}
          value={formik.values.login}
        />
        {formik.touched.login && formik.errors.login ? (
         <div>{formik.errors.login}</div>
       ) : null}

        <input 
          placeholder="Password" 
          type="password" 
          name="password"
          onChange={formik.handleChange}
          onBlur={formik.handleBlur}
          value={formik.values.password}  
        />

        {formik.touched.password && formik.errors.password ? (
         <div>{formik.errors.password}</div>
       ) : null}

        <input type="submit" />
      </div>
      <div>
        <h1>Login</h1>
        <input  placeholder="Login" type="text" name="login" />
        <input placeholder="Password" type="password" name="password" />
        <input type="submit" value="Login" />
      </div>
    </div>
  )
}

export default Form;
