import React from 'react';
// import {Formik, Form, Field, ErrorMessage} from 'formik'
import { useState, useEffect } from 'react';



const Form = () => {

  const useForm = () => {
    const [values, setValues] = useState({
      email: '',
      password: '',
    });
    const [errors, setErrors] = useState ({});

    const handleChange = e => {
      const {name, value} = e.target;
      setValues({
        ...values,
        [e.target.name]: e.target.value
      })
    }

    const handleSubmit = (e) => {
        e.preventDefault();

    }
    return {handleChange, values, handleSubmit};
  };

  const validateInput = (values) => {
    let errors = {};

    if (!values.name.trim()) {
      errors.name = "Username required";
    }

    if (!values.password) {
      errors.password = "Password is required";
    } else if (values.password.length < 8) {
      errors.password = "Password should be 8 or more characters";
    }
    return errors;
  }

  const {handleChange, values, handleSubmit} = useForm();

  return (
    <div>
      <h1>Check components/Form.js file</h1>
      <p>Below you fill find register and login forms.</p>
      <p>Validate values of each field on your preferences. The more secure the better.</p>
      <p>After user submitted register form inform him about it and store user data somewhere.</p>
      <p>When submiting login form check if such a user has been registered.</p>
      <p>If user exists hide all the forms and show a test 'Logged in successfully'</p>
      <p>If user does not exist then show an error message below login button</p>
      <div onSubmit={handleSubmit}>
        <h1>Register</h1>
        <input value={values.login} onChange={handleChange} placeholder="Login" type="text" name="login"/>
        <input value={values.password} onChange={handleChange} placeholder="Password" type="password" name="password"/>
        <input type="submit" value="Register" />
      </div>
      <div>
        <h1>Login</h1>
        <input value={values.login}  placeholder="Login" type="text" name="login" />
        <input value={values.password} placeholder="Password" type="password" name="password" />
        <input type="submit" value="Login" />
      </div>
    </div>
  )
}

export default Form;
