import React from 'react';

const Drag = () => {

  function dragStart(event) {
    event.dataTransfer.setData("Text", event.target.id);
  }

  function drop(event) {
    event.preventDefault();
    var data = event.dataTransfer.getData("Text");
    event.target.appendChild(document.getElementById(data));
  }

  function allowDrop(event) {
    event.preventDefault();
  }

  return (

      <div>
            <h1>Check components/Drag.js file</h1>
            <p>Using onDrag onDragEnter and onDragLeave events move the orage square from one box to another</p>
            <div style={{
              display: 'flex',
              justifyContent: 'space-around'
            }}>
              <div style={{
                width: 200,
                height: 200,
                border: '1px solid black',
              }}>
                <div style={{
                  width: 50,
                  height: 50,
                  backgroundColor: 'orange'
                }}
                id="dragtarget" draggable="true" onDrag={dragStart}
                />
              </div>
              <div style={{
                width: 200,
                height: 200,
                border: '1px solid black',
              }} onDragEnter={drop} onDragLeave={allowDrop} />
            </div>
          </div>
  )
}

export default Drag;
